                    for(i=0;i<=array.pooja_samagri_details.length;i++)
                    {
                        str+='<tr style="display: table-row; opacity: 1;">';
                        str+='<td>'+(i+1)+'</td>';
                        str+='<td><img src="../uploads/pooja_samagri/'+array.pooja_samagri_details[i].psm_image+'" width="50px" height="50px"  </td>';
                        str+='<td>'+array.pooja_samagri_details[i].psm_name+'</td>';
                        str+='<td>'+array.pooja_samagri_details[i].psm_description+'</td>';
                        str+='<td>';
                        str+='<a href="<?=base_url()?>admin_panel/PoojaSamagri/edit/'+array.pooja_samagri_details[i].psm_id+'"><i class="fa fa-edit"></i></a>';
                        str+='<a href="disable/'+array.pooja_samagri_details[i].psm_id+'"><i title="Disable" class="fa fa-ban"></i></a>';
                        str+='</td>';
                        str+='</tr>';
                        $("#itemContainer").html(str);
                    }
                },
                error: function(data)
                {
                    alert("Page Or Folder Not Created..!!");
                }
            });
        });
</script>
    